import XCTest
@testable import RileyLinkBLEKit

class RFPacketTests: XCTestCase {

    func testDecodeRF() {
        let response = Data(hexadecimalString: "4926a965a5d1a8dab0e5635635555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555559a35")
        let packet = RFPacket(rfspyResponse: response!)!
    XCTAssertEqual("a965a5d1a8dab0e5635635555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555559a35", packet.data.hexadecimalString)
        XCTAssertEqual(-37, packet.rssi)
    }

}
